import React, { useState, useEffect, useRef } from 'react';
import api from '../../configs/Apis';
import './AIChatbot.css';

const AIChatbot = ({ isOpen, onClose, userId = null }) => {
    const [sessionId, setSessionId] = useState(null);
    const [messages, setMessages] = useState([]);
    const [inputMessage, setInputMessage] = useState('');
    const [isLoading, setIsLoading] = useState(false);
    const [isTyping, setIsTyping] = useState(false);
    const messagesEndRef = useRef(null);
    const inputRef = useRef(null);

    // Auto scroll to bottom
    const scrollToBottom = () => {
        messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
    };

    useEffect(() => {
        scrollToBottom();
    }, [messages]);

    // Kh·ªüi t·∫°o chat session khi m·ªü chatbot
    useEffect(() => {
        if (isOpen && !sessionId) {
            startNewChat();
        }
    }, [isOpen]);

    // Focus input khi m·ªü chatbot
    useEffect(() => {
        if (isOpen && inputRef.current) {
            setTimeout(() => inputRef.current.focus(), 100);
        }
    }, [isOpen]);

    const startNewChat = async () => {
        try {
            setIsLoading(true);
            const response = await api.post(`/chatbot/start`, {
                userId: userId
            });

            if (response.data.success) {
                setSessionId(response.data.sessionId);
                setMessages([response.data.initialMessage]);
            }
        } catch (error) {
            console.error('Error starting chat:', error);
            setMessages([{
                role: 'system',
                content: 'Xin l·ªói, c√≥ l·ªói x·∫£y ra khi kh·ªüi t·∫°o chat. Vui l√≤ng th·ª≠ l·∫°i.',
                timestamp: new Date()
            }]);
        } finally {
            setIsLoading(false);
        }
    };

    const sendMessage = async (e) => {
        e.preventDefault();
        
        if (!inputMessage.trim() || !sessionId || isLoading) return;

        const userMessage = {
            role: 'user',
            content: inputMessage.trim(),
            timestamp: new Date()
        };

        // Th√™m tin nh·∫Øn c·ªßa user v√†o chat
        setMessages(prev => [...prev, userMessage]);
        setInputMessage('');
        setIsTyping(true);

        try {
            const response = await api.post(`/chatbot/message`, {
                sessionId: sessionId,
                message: userMessage.content,
                userId: userId
            });

            if (response.data.success) {
                const aiMessage = {
                    role: 'assistant',
                    content: response.data.response,
                    timestamp: new Date(),
                    metadata: {
                        intent: response.data.intent,
                        confidence: response.data.confidence,
                        entities: response.data.entities
                    }
                };

                setMessages(prev => [...prev, aiMessage]);
            }
        } catch (error) {
            console.error('Error sending message:', error);
            const errorMessage = {
                role: 'assistant',
                content: 'Xin l·ªói, c√≥ l·ªói x·∫£y ra. Vui l√≤ng th·ª≠ l·∫°i.',
                timestamp: new Date()
            };
            setMessages(prev => [...prev, errorMessage]);
        } finally {
            setIsTyping(false);
        }
    };

    const handleKeyPress = (e) => {
        if (e.key === 'Enter' && !e.shiftKey) {
            sendMessage(e);
        }
    };

    const formatMessage = (message) => {
        // X·ª≠ l√Ω xu·ªëng d√≤ng trong tin nh·∫Øn
        return message.content.split('\n').map((line, index) => (
            <span key={index}>
                {line}
                {index < message.content.split('\n').length - 1 && <br />}
            </span>
        ));
    };

    const getMessageTime = (timestamp) => {
        return new Date(timestamp).toLocaleTimeString('vi-VN', {
            hour: '2-digit',
            minute: '2-digit'
        });
    };

    const getIntentIcon = (intent) => {
        const intentIcons = {
            'product_search': 'üîç',
            'category_info': 'üìÇ',
            'price_inquiry': 'üí∞',
            'stock_check': 'üì¶',
            'product_comparison': '‚öñÔ∏è',
            'recommendation': 'üí°',
            'greeting': 'üëã',
            'farewell': 'üëã',
            'general_question': '‚ùì'
        };
        return intentIcons[intent] || 'üí¨';
    };

    if (!isOpen) return null;

    return (
        <div className="ai-chatbot-overlay" onClick={onClose}>
            <div className="ai-chatbot-container" onClick={(e) => e.stopPropagation()}>
                {/* Header */}
                <div className="ai-chatbot-header">
                    <div className="ai-chatbot-title">
                        <span className="ai-icon">ü§ñ</span>
                        <span>AI Tr·ª£ l√Ω</span>
                    </div>
                    <button className="ai-chatbot-close" onClick={onClose}>
                        ‚úï
                    </button>
                </div>

                {/* Messages Container */}
                <div className="ai-chatbot-messages">
                    {messages.map((message, index) => (
                        <div
                            key={index}
                            className={`ai-message ${message.role === 'user' ? 'user' : 'assistant'}`}
                        >
                            <div className="ai-message-content">
                                {message.role === 'assistant' && message.metadata?.intent && (
                                    <div className="ai-message-intent">
                                        {getIntentIcon(message.metadata.intent)}
                                        <span className="intent-text">
                                            {message.metadata.intent.replace('_', ' ')}
                                        </span>
                                    </div>
                                )}
                                <div className="ai-message-text">
                                    {formatMessage(message)}
                                </div>
                                <div className="ai-message-time">
                                    {getMessageTime(message.timestamp)}
                                </div>
                            </div>
                        </div>
                    ))}
                    
                    {isTyping && (
                        <div className="ai-message assistant">
                            <div className="ai-message-content">
                                <div className="ai-typing">
                                    <span></span>
                                    <span></span>
                                    <span></span>
                                </div>
                            </div>
                        </div>
                    )}
                    
                    <div ref={messagesEndRef} />
                </div>

                {/* Input Form */}
                <form className="ai-chatbot-input-form" onSubmit={sendMessage}>
                    <div className="ai-input-container">
                        <input
                            ref={inputRef}
                            type="text"
                            value={inputMessage}
                            onChange={(e) => setInputMessage(e.target.value)}
                            onKeyPress={handleKeyPress}
                            placeholder="Nh·∫≠p c√¢u h·ªèi c·ªßa b·∫°n..."
                            disabled={isLoading || !sessionId}
                            className="ai-message-input"
                        />
                        <button
                            type="submit"
                            disabled={isLoading || !sessionId || !inputMessage.trim()}
                            className="ai-send-button"
                        >
                            {isLoading ? '‚è≥' : 'üì§'}
                        </button>
                    </div>
                    
                    {/* Quick Actions */}
                    <div className="ai-quick-actions">
                        <button
                            type="button"
                            onClick={() => setInputMessage('T√¨m s·∫£n ph·∫©m')}
                            className="ai-quick-action-btn"
                        >
                            üîç T√¨m s·∫£n ph·∫©m
                        </button>
                        <button
                            type="button"
                            onClick={() => setInputMessage('Xem danh m·ª•c')}
                            className="ai-quick-action-btn"
                        >
                            üìÇ Danh m·ª•c
                        </button>
                        <button
                            type="button"
                            onClick={() => setInputMessage('ƒê·ªÅ xu·∫•t s·∫£n ph·∫©m')}
                            className="ai-quick-action-btn"
                        >
                            üí° ƒê·ªÅ xu·∫•t
                        </button>
                    </div>
                </form>

                {/* Footer */}
                <div className="ai-chatbot-footer">
                    <small>AI ƒë∆∞·ª£c hu·∫•n luy·ªán v·ªõi d·ªØ li·ªáu s·∫£n ph·∫©m th·ª±c t·∫ø</small>
                </div>
            </div>
        </div>
    );
};

export default AIChatbot;
